import React, { useState } from 'react';
import Star from './Star';
import PropTypes from 'prop-types';

const StarRating = ({ initialRating, maxRating, onRate }) => {
  const [rating, setRating] = useState(initialRating);

  const handleStarClick = (index) => {
    setRating(index + 1);
    if (onRate) onRate(index + 1);
  };

  return (
    <div style={{ display: 'flex' }}>
      {Array.from({ length: maxRating }, (_, index) => (
        <Star 
          key={index}
          filled={index < rating}
          onClick={() => handleStarClick(index)}
        />
      ))}
    </div>
  );
};

StarRating.propTypes = {
  initialRating: PropTypes.number,
  maxRating: PropTypes.number,
  onRate: PropTypes.func,
};

StarRating.defaultProps = {
  initialRating: 0,
  maxRating: 5,
};

const StarDisplay = ({ initialRating, maxRating }) => {
  const [rating] = useState(initialRating);


  return (
    <div style={{ display: 'flex' }}>
      {Array.from({ length: maxRating }, (_, index) => (
        <Star 
          key={index}
          filled={index < rating}
        />
      ))}
    </div>
  );
};

StarDisplay.propTypes = {
  initialRating: PropTypes.number,
  maxRating: PropTypes.number,
};

StarDisplay.defaultProps = {
  initialRating: 0,
  maxRating: 5,
};

const Rate = ({star, newRating}) => {
    const handleRatingChange = (newRating) => {
      console.log('New rating:', newRating);
      star={newRating};
    };

    return (
      <div>
        <StarRating 
          initialRating={newRating} 
          maxRating={5} 
          onRate={handleRatingChange} 

        />
      </div>
    );
  };

  const DisplayRate = ({star}) => {

    return (
      <div>
        <StarDisplay
          initialRating={star} 
          maxRating={5} 
        />
      </div>
    );
  }


  export {Rate, DisplayRate};
